r[i][j]=
    \begin{cases}
      j, & \text{if}\ i=0 \\
      i, & \text{if}\ j=0 \\
      r[i-1][j-1], & \text{if}\ X_i=Y_j \\
      \min(r[i-1][j], r[i][j-1], r[i-1][j-1]) + 1, & \text{if} \ X_i\neq Y_j
    \end{cases}
